import React from "react";
import "./App.css"
import EntryForm from "./Pages/dummyForm/EntryForm";
import {useSelector,useDispatch} from "react-redux"
import {incNumber,decNumber} from "./actions/index"
function App() {

const myState = useSelector(state => state.changeTheNumber)
const dispatch = useDispatch()

  return(


<>
<div className="container">
  <h1>Increment/Decrement counter</h1>
  <h4>Using react and redux</h4>
  <div className="quantity ">
    <a href="" title='Decrement' className="quantity__minus"><span>-</span></a>
<input type="text" className="quantity__input" name='quantity' value={myState} />
    <a href="" title='Decrement' className="quantity__plus " onClick={()=>dispatch(incNumber)}><span>+</span></a>
  </div>
</div>
</>

  )
}

export default App;
