import React from 'react'
import "./App.css"

function Child(number1) {
console.log(number1.number1)
const reset=()=>{
    number1.number1=""
    number1.number2=""
}

    return (
        <>
        <h4>
            `The value for number 1 is "{number1.number1}" and the value for number 2 is "{number1.number2}" `
        </h4>
        <button onClick={()=>reset()}></button>
        </>
    )
}

export default Child
