import {React,useRef} from 'react'
import SignaturePad from 'react-signature-canvas'
import './sign.css'


function Sign() {
    let sigPad=useRef({})
    const clear=()=>{
        sigPad.current.clear()
    }

    return (
        <div className='signature'>
            <button onClick={clear}>Clear</button>
        <SignaturePad ref={sigPad} />
        </div>
    )
}

export default Sign
